INSERT INTO `statics` (`id`, `s_title`, `s_slug`, `s_type`, `s_active`, `s_md5`, `s_content`, `created_at`, `updated_at`) VALUES
(1, 'Chính sách mua hàng & thanh toán', NULL, 1, 1, NULL, '<p><strong>HƯỚNG DẪN MUA H&Agrave;NG V&Agrave; THANH TO&Aacute;N:</strong></p>\r\n\r\n<p>Để thuận lợi cho việc mua h&agrave;ng của qu&yacute; kh&aacute;ch,&nbsp;sieuthiruoungoai.com&nbsp;c&oacute; 2 h&igrave;nh thức mua h&agrave;ng như sau:</p>\r\n\r\n<p>- Mua h&agrave;ng theo phương thức gọi điện thoại hoặc chat.</p>\r\n\r\n<p>- Mua h&agrave;ng theo phương thức trực tuyến.</p>\r\n\r\n<p><strong>I-&nbsp;</strong><strong>Mua h&agrave;ng theo phương thức gọi điện thoại hoặc chat:</strong></p>\r\n\r\n<p>- Qu&yacute; kh&aacute;ch lựa chọn những sản phẩm rượu cần mua. Sau đ&oacute; li&ecirc;n hệ số điện thoại hotline&nbsp;<strong>HCM1</strong>:&nbsp;<strong>0902.385.002&nbsp; |&nbsp; HCM2: 0983.182.887&nbsp; |&nbsp; H&agrave; Nội 1: 093.12.16.002&nbsp; |&nbsp; H&agrave; Nội 2:&nbsp;0966.998.618&nbsp; |&nbsp; Đ&agrave; Nẵng: 0979.61.2820&nbsp;</strong>&nbsp;hoặc chat box trực tiếp tr&ecirc;n website, Zalo, Viber, SMS&nbsp;để đặt mua sản phẩm. L&uacute;c đ&oacute;, sẽ c&oacute; nh&acirc;n vi&ecirc;n hướng dẫn qu&yacute; kh&aacute;ch từng bước c&aacute;ch thức mua h&agrave;ng. Qu&yacute; kh&aacute;ch vui l&ograve;ng để lại địa chỉ v&agrave; th&ocirc;ng tin c&aacute; nh&acirc;n, số điện thoại c&aacute; nh&acirc;n để&nbsp;sieuthiruoungoai.com&nbsp;ch&uacute;ng t&ocirc;i li&ecirc;n hệ giao h&agrave;ng, xuất h&oacute;a đơn v&agrave; thanh to&aacute;n chi ph&iacute;.</p>\r\n\r\n<p><strong>II-&nbsp;</strong><strong>Mua h&agrave;ng theo phương thức trực tuyến: -&nbsp;</strong>Theo quy định của bộ c&ocirc;ng thương th&igrave; rượu ngoại kh&ocirc;ng được b&aacute;n online n&ecirc;n Qu&yacute; Kh&aacute;ch h&agrave;ng muốn đặt h&agrave;ng vui l&ograve;ng li&ecirc;n hệ trực tiếp với nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng hoặc số hotline để được hỗ trợ.</p>\r\n\r\n<p>Hoặc Qu&yacute; kh&aacute;ch h&agrave;ng gửi th&ocirc;ng tin y&ecirc;u cầu b&aacute;o gi&aacute; trực tiếp v&agrave;o mục b&aacute;o gi&aacute; sỉ tr&ecirc;n website của ch&uacute;ng t&ocirc;i hoăc gửi trực tiếp y&ecirc;u cầu v&agrave;o mail:&nbsp;<a href=\"mailto:ruoungoai@sieuthiruoungoai.com\">ruoungoai@sieuthiruoungoai.com</a>, ch&uacute;ng t&ocirc;i sẽ li&ecirc;n hệ lại với Qu&yacute; kh&aacute;ch h&agrave;ng trong thời gian sớm nhất.</p>\r\n\r\n<p><strong>III. Phương Thức Thanh to&aacute;n:</strong></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch h&agrave;ng đặt h&agrave;ng c&oacute; thể thanh chuyển khoản to&aacute;n trước (Nếu kh&aacute;ch h&agrave;ng ở tỉnh th&agrave;nh kh&aacute;c ngo&agrave;i TP.HCM, HN, Đ&agrave; Nẵng) v&agrave; kh&aacute;ch h&agrave;ng c&oacute; thể thanh to&aacute;n chuyển khoản trức hoặc thanh to&aacute;n trực tiếp cho nh&acirc;n vi&ecirc;n giao h&agrave;ng khi nhận h&agrave;ng nếu kh&aacute;ch h&agrave;ng ở tại TP.HCM, H&agrave; Nội v&agrave; Đ&agrave; nẵng.</p>', '2020-05-19 10:14:18', '2020-05-19 10:14:18'),
(2, 'Chính sách giao nhận', NULL, 2, 1, NULL, '<p><strong>Hỏi:</strong>&nbsp;Bao l&acirc;u t&ocirc;i nhận được h&agrave;ng sau khi thanh to&aacute;n?&nbsp;<img alt=\"Cool\" src=\"https://www.sieuthiruoungoai.com/javascripts/tiny_mce/plugins/emotions/img/smiley-cool.gif\" /></p>\r\n\r\n<p><strong>Trả lời:</strong>&nbsp;Sau khi bạn ho&agrave;n tất việc đặt h&agrave;ng v&agrave; thanh to&aacute;n tiền h&agrave;ng (ngo&agrave;i tỉnh th&agrave;nh phố HCM) th&igrave; trong v&ograve;ng 7 ng&agrave;y ch&uacute;ng t&ocirc;i sẽ chuyển h&agrave;ng đến tận nơi cho qu&yacute; kh&aacute;ch h&agrave;ng, c&ograve;n đối với kh&aacute;ch h&agrave;ng tại nội th&agrave;nh TP.HCM, H&Agrave; NỘI, ĐỒNG NAI, NHA TRANG, Đ&Agrave; NẴNG, ĐĂK LĂK. th&igrave; ch&uacute;ng t&ocirc;i sẽ giao h&agrave;ng trong v&ograve;ng 24h ( c&oacute; thể thanh to&aacute;n tiền trước hoặc sau thanh to&aacute;n tiền h&agrave;ng sau khi đ&atilde; nhận đủ h&agrave;ng),</p>\r\n\r\n<p>- Đối với c&aacute;c kh&aacute;ch h&agrave;ng kh&ocirc;ng thuộc nội th&agrave;nh th&agrave;nh phố c&aacute;c tỉnh n&oacute;i tr&ecirc;n, Qu&yacute; kh&aacute;ch h&agrave;ng mua lẻ đặt h&agrave;ng vui l&ograve;ng thanh to&aacute;n tiền trước qua t&agrave;i khoản của sieuthiruoungoai.com. Sau khi nhận được đơn đặt h&agrave;ng v&agrave; tiền h&agrave;ng, ch&uacute;ng t&ocirc;i sẽ chuyển h&agrave;ng trong v&ograve;ng 24h.</p>\r\n\r\n<p>- Đối với kh&aacute;ch h&agrave;ng kh&ocirc;ng thuộc nội th&agrave;nh th&agrave;nh phố kể tr&ecirc;n, kh&aacute;ch h&agrave;ng mua số lượng lớn, sau khi thỏa thuận 2 b&ecirc;n v&agrave; tiến h&agrave;nh k&yacute; hợp đồng mua b&aacute;n giữa sieuthiruoungoai.com v&agrave; Qu&yacute; kh&aacute;ch h&agrave;ng, kh&aacute;ch h&agrave;ng sẽ đặt cọc 50% tổng số đơn h&agrave;ng, v&agrave; thanh to&aacute;n số tiền c&ograve;n lại (50%) sau khi kh&aacute;ch h&agrave;ng đ&atilde; nhận đủ h&agrave;ng (bao gồm: số lượng H&agrave;ng như trong hợp đồng, H&oacute;a đơn VAT, Bản thanh l&yacute; hợp đồng).</p>', '2020-05-19 10:24:03', '2020-05-19 10:24:03'),
(5, 'Về chúng tôi', NULL, 3, 1, NULL, '<p>&ldquo;Rượu&rdquo; &ndash; kh&ocirc;ng đơn thuần l&agrave; một loại thức uống th&ocirc;ng thường m&agrave; c&oacute; thể n&oacute;i rượu l&agrave; thực thể c&oacute; hồn. Vậy l&agrave;m thế n&agrave;o để cho người uống hiểu hết được c&aacute;i hồn của Rượu, &ldquo;<strong>Si&ecirc;u thị rượu ngoại</strong>&rdquo; ra đời với mong muốn cung cấp cho Qu&yacute; kh&aacute;ch những d&ograve;ng rượu hảo hạng,mới lạ&hellip;</p>\r\n\r\n<p>Rượu bị ảnh hưởng bởi lịch sử, văn ho&aacute;, địa l&yacute;, nh&acirc;n chủng học,&hellip;v&igrave; vậy lượng kiến thức về rượu thật rộng lớn. Do đ&oacute;, &ldquo;<strong>Si&ecirc;u thị rượu ngoại</strong>&rdquo; kh&ocirc;ng chỉ đơn thuần l&agrave; một Website giới thiệu c&aacute;c sản phẩm rượu ngoại nhập khẩu , &ldquo;<strong>Si&ecirc;u thị rượu ngoại</strong>&rdquo; c&ograve;n mong muốn g&oacute;p phần tập hợp, hệ thống c&aacute;c kiến thức về văn h&oacute;a, lịch sử, địa l&yacute;... cho đến c&aacute;ch thức lựa chọn, bảo quản, thưởng thức... c&aacute;c d&ograve;ng rượu kh&aacute;c nhau tr&ecirc;n thế giới để những người say m&ecirc; hoặc muốn t&igrave;m hiểu về &ldquo;văn h&oacute;a rượu&rdquo;c&oacute; thể c&ugrave;ng chia sẻ, n&acirc;ng cao tầm hiểu biết.</p>\r\n\r\n<p>Ng&agrave;y nay với thủ thuật l&agrave;m giả tinh vi, những người l&agrave;m rượu giả c&oacute; thể cho ra đời những chai rượu giả kh&oacute; c&oacute; thể ph&aacute;t hiện. V&igrave; vậy t&igrave;nh h&igrave;nh rượu ngoại tr&ecirc;n thị trường thật phức tạp, kh&aacute;ch h&agrave;ng muốn mua một chai rượu ngon để thưởng thức hoặc l&agrave;m qu&agrave; biếu th&igrave; cũng rất l&uacute;ng t&uacute;ng.</p>\r\n\r\n<p>Một vấn đề kh&oacute; khăn nữa l&agrave; những chai rượu khi kh&ocirc;ng được bảo quản tốt: như trưng b&agrave;y qu&aacute; l&acirc;u, nơi cất giữ n&oacute;ng, ẩm, &aacute;nh s&aacute;ng qu&aacute; cao&hellip; l&agrave;m cho những chai rượu bị biến đổi về phẩm chất, hoặc sinh ra những chất độc hại.</p>\r\n\r\n<p>Khi mua h&agrave;ng tại&nbsp;si&ecirc;u thị rượu ngoại&nbsp;của ch&uacute;ng t&ocirc;i, qu&yacute; kh&aacute;ch sẽ được kh&aacute;m ph&aacute; một thế giới phong ph&uacute; về rượu, từ c&aacute;c loại&nbsp;rượu ngoại&nbsp;như: rượu mạnh Ch&acirc;u &Acirc;u, Rượu Ch&acirc;u &Aacute;, Ch&acirc;u Phi,&nbsp;rượu Vang đến từ khắp nơi tr&ecirc;n thế giới, &hellip;đến c&aacute;c loại&nbsp;rượu trong nước. Ch&uacute;ng t&ocirc;i cung cấp những chai rượu được nhập ch&iacute;nh ngạch v&agrave; từ ch&iacute;nh h&atilde;ng, mỗi chai rượu được ch&uacute;ng t&ocirc;i cung cấp phải lu&ocirc;n c&oacute; xuất xứ r&otilde; r&agrave;ng, đảm bảo chất lượng v&agrave; lu&ocirc;n được bảo quản tốt để c&oacute; thể đ&aacute;p ứng về nhu cầu của qu&yacute; kh&aacute;ch một c&aacute;ch tốt nhất.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i chuy&ecirc;n ph&acirc;n phối sỹ v&agrave; lẻ c&aacute;c sản phẩm rượu ngoại. Nếu Qu&yacute; kh&aacute;ch h&agrave;ng mua rượu gi&aacute; sỹ vui l&ograve;ng li&ecirc;n hệ để c&oacute; gi&aacute; tốt nhất.</p>\r\n\r\n<p>Hotline:<strong>&nbsp;0902.385.002&nbsp; |&nbsp; 0983.182.887&nbsp; |&nbsp; &nbsp;0978.003.117&nbsp;</strong></p>\r\n\r\n<p>Email: ruoungoai@sieuthiruoungoai.com</p>\r\n\r\n<p>Nếu Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; thắc mắc g&igrave; hoặc cần li&ecirc;n hệ với ch&uacute;ng t&ocirc;i vui l&ograve;ng gọi v&agrave;o hotline của ch&uacute;ng t&ocirc;i hoặc gửi email cho ch&uacute;ng t&ocirc;i. Ch&uacute;ng t&ocirc;i sẽ trả lời cho Qu&yacute; kh&aacute;ch h&agrave;ng trong thời gian sớm nhất.</p>', '2020-05-19 10:22:29', '2020-05-19 10:22:29');

